import {useReducer} from "react";
    import CartContext from "./cart-context";

    const defaultCartState={
        items:[],
        totalAmount: 0
    };
const cartReducer =(state,action) => {
    if(action.type==="ADD")
    {
        const updatedAmount=state.totalAmount+action.item.price*action.amount;
        const updatedItems=state.items.concat(action.item);//new array
        // const existingCartItemIndex= state.items.findIndex(item=>item.id===action.item.id);
        // const existingCartItems=state.item[existingCartItemIndex];
        // let updatedItem;
        // let updatedItems;


        
        return{
            items:updatedItems,
            totalAmount:updatedAmount

        };
    }
    return defaultCartState;
};
const CartProvider =(props)=>{
   const[cartState,dispatchCartState] = useReducer(cartReducer,defaultCartState)
    const addItemHandler =item=>{
        dispatchCartState({type:"ADD",item:item});
    };
    const removeItemHandler =item=>{
        dispatchCartState({type:"REMOVE",item:item});
    };
    
    const cartContext ={
        items:cartState.items,
        totalAmount:cartState.totalAmount,
        addItem:addItemHandler,
        removeItem:removeItemHandler
        
    };
    return <CartContext.Provider value={cartContext}>
        {props.children}
    </CartContext.Provider>


}
export default CartProvider;
